import React, { useState } from 'react';
import { createContact } from '../../api/contactApi';

// Тип для нового контакта
interface NewContact {
  name: string;
  phone: string;
}

const ContactForm: React.FC = () => {
  const [name, setName] = useState<string>(''); // Типизируем как строку
  const [phone, setPhone] = useState<string>(''); // Типизируем как строку

  // Обработчик формы
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const newContact: NewContact = { name, phone }; // Создаем объект с типом NewContact
      await createContact(newContact); // Отправляем данные на сервер
      setName('');
      setPhone('');
      alert('Contact added successfully!');
    } catch (error) {
      console.error('Error adding contact:', error);
      alert('Error adding contact');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add New Contact</h2>
      <div>
        <label>Name:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)} // Обработчик изменения для name
          required
        />
      </div>
      <div>
        <label>Phone:</label>
        <input
          type="text"
          value={phone}
          onChange={(e) => setPhone(e.target.value)} // Обработчик изменения для phone
          required
        />
      </div>
      <button type="submit">Add Contact</button>
    </form>
  );
};

export default ContactForm;
