import React, { useEffect, useState } from 'react';
import { getContacts, deleteContact } from '../../api/contactApi';
import Modal from '../Modal/Modal';
import DeleteNum from '../Modal/Delete/Delete';
import EditNum from '../Modal/Edit/Edit';


// Определяем интерфейс для контакта
interface Contact {
  id: number;
  name: string;
  phone: string;
}

const ContactList: React.FC = () => {
  // Типизируем состояние как массив объектов типа Contact
  const [contacts, setContacts] = useState<Contact[]>([]);
  const [modalActive, setModalActive] = useState(false);
  const [contactToDelete, setContactToDelete] = useState<Contact | null>(null); // Контакт для удаления

  useEffect(() => {
    const fetchContacts = async () => {
      try {
        const data = await getContacts();
        setContacts(data);
      } catch (error) {
        console.error('Error fetching contacts:', error);
      }
    };
    fetchContacts();
  }, []);

  // Функция для открытия модального окна с выбранным контактом
  const handleDeleteClick = (contact: Contact) => {
    setContactToDelete(contact);
    setModalActive(true); // Открываем модальное окно
  };

  // Функция для удаления контакта
  const handleDeleteConfirm = async () => {
    if (contactToDelete) {
      try {
        await deleteContact(contactToDelete.id); // Удаляем контакт через API
        setContacts(contacts.filter(contact => contact.id !== contactToDelete.id)); // Обновляем список контактов
        setModalActive(false); // Закрываем модальное окно
      } catch (error) {
        console.error('Error deleting contact:', error);
      }
    }
  };

  const handleDeleteCancel = () => {
    setModalActive(false); // Закрываем модальное окно без удаления
  };

  return (
    <div>
      <h2>Номера</h2>
      <ul>
        {contacts.map((contact) => (
          <li key={contact.id}>
            {contact.name} - {contact.phone}
            <button onClick={() => handleDeleteClick(contact)}>Удалить</button>
          </li>
        ))}
      </ul>

      {/* Модальное окно удаления */}
      <Modal active={modalActive} setActive={setModalActive}>
        <DeleteNum
          contact={contactToDelete}
          onDelete={handleDeleteConfirm}
          onCancel={handleDeleteCancel}
        />
      </Modal>
    </div>
  );
};

export default ContactList;
