import React, { useState } from 'react';
import { Contact } from '../../../types/contact';

interface EditNumProps {
  contactsec: Contact | null;
  onSave: (updatedContact: Contact) => void;
  onCancel: () => void;
}

const EditNum: React.FC<EditNumProps> = ({ contactsec, onSave, onCancel }) => {
  const [name, setName] = useState(contactsec?.name || '');
  const [phone, setPhone] = useState(contactsec?.phone || '');

  const handleSave = () => {
    if (contactsec) {
      onSave({ ...contactsec, name, phone });
    }
  };

  return (
    <div>
      <h3>Редактировать контакт</h3>
      <label>
        Имя:
        <input
          type="text"
          value={name}
          onChange={e => setName(e.target.value)}
        />
      </label>
      <label>
        Телефон:
        <input
          type="text"
          value={phone}
          onChange={e => setPhone(e.target.value)}
        />
      </label>
      <button onClick={handleSave}>Сохранить</button>
      <button onClick={onCancel}>Отмена</button>
    </div>
  );
};

export default EditNum;
